import { MessageService } from 'primeng/api';
import * as i0 from "@angular/core";
import * as i1 from "angularfire2/database";
import * as i2 from "@angular/forms";
import * as i3 from "primeng/inputtext";
import * as i4 from "primeng/button";
export class BookingComponent {
    constructor(db) {
        this.db = db;
        // this.db.object('booking/0').set({id : '0',tel : '0905555555',dayNumb : '1' ,priceAll : '5000'})
        this.getStartedBooking();
        this.getStartedUser();
    }
    async getStartedBooking() {
        var booking;
        await this.getBookingFromRealtimeDB().then(value => {
            booking = value;
            this._bookingList = booking;
        });
        // console.log(this._bookingList)
        this._idP = this._bookingList[this._bookingList.length - 1].id + 1;
    }
    getBookingFromRealtimeDB() {
        return new Promise((resolve, reject) => {
            this.db.list('booking').valueChanges().subscribe(value => {
                resolve(value);
            });
        });
    }
    async getStartedUser() {
        var user;
        await this.getLoginFromRealtimeDB().then(value => {
            user = value;
            this._user = user;
        });
        console.log(this._user);
        this._uName = this._user[2].firstname + " " + this._user[2].lastname;
        this._email = this._user[2].email;
    }
    getLoginFromRealtimeDB() {
        return new Promise((resolve, reject) => {
            this.db.list('login').valueChanges().subscribe(value => {
                resolve(value);
            });
        });
    }
    ngOnInit() {
    }
    async addBooking() {
        var data = {
            // id: this._id,
            tel: this._tel,
            dayNumb: this._dayNumb,
            // dayIn: this._dayIn,
            // dayOut: this._dayOut,
            priceAll: this._priceAll
        };
        await this.db.object('booking/' + (String(this._idP))).set(data);
        await this.getStartedBooking();
    }
