import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { MessageService } from 'primeng/api';
let BookingComponent = class BookingComponent {
    constructor(db) {
        this.db = db;
        // this.db.object('booking/0').set({id : '0',tel : '0905555555',dayNumb : '1' ,priceAll : '5000'})
        this.getStarted();
        this.getLogins();
    }
    async getStarted() {
        var booking;
        await this.getBookingFromRealtimeDB().then(value => {
            booking = value;
            this._bookingList = booking;
        });
        console.log(this._bookingList);
        this._id = this._bookingList[this._bookingList.length - 1].id + 1;
    }
    getBookingFromRealtimeDB() {
        return new Promise((resolve, reject) => {
            this.db.list('booking').valueChanges().subscribe(value => {
                resolve(value);
            });
        });
    }
    async getLogins() {
        var login;
        await this.getLogin().then(value => {
            login = value;
            this._login = login;
        });
        console.log(this._login);
    }
    getLogin() {
        return new Promise((resolve, reject) => {
            this.db.list('login').valueChanges().subscribe(value => {
                resolve(value);
            });
        });
    }
    // constructor(private primengConfig: PrimeNGConfig) {}
    ngOnInit() {
        // this._nameCus = tel;
    }
    async addBooking() {
        var data = {
            // id: this._id,
            tel: this._tel,
            dayNumb: this._dayNumb,
            // dayIn: this._dayIn,
            // dayOut: this._dayOut,
            priceAll: this._priceAll
        };
        await this.db.object('booking/' + (String(this._id))).set(data);
        await this.getStarted();
    }
};
BookingComponent = __decorate([
    Component({
        selector: 'app-booking',
        templateUrl: './booking.component.html',
        styleUrls: ['./booking.component.css'],
        providers: [MessageService]
    })
], BookingComponent);
export { BookingComponent };
class Booking {
}
class Login {
}
